import NestedArray = require( './index' );


// TESTS //

// The function returns an ndarray...
{
	const shape = [ 2, 2 ];
	const strides = [ 2, 1 ];

	let x = NestedArray( 'float64', [ 1, 2, 3, 4 ], shape, strides, 0, 'row-major' ); // $ExpectType ndarray
	x = new NestedArray( 'float64', [ 1, 2, 3, 4 ], shape, strides, 0, 'row-major' ); // $ExpectType ndarray

	const buffer = new Int32Array( [ 1, 2, 3 ] );
	const order = 'column-major';

	x = NestedArray( 'int32', buffer, shape, strides, 0, order ); // $ExpectType ndarray
	x = new NestedArray( 'int32', buffer, shape, strides, 0, order ); // $ExpectType ndarray

	x = NestedArray( 'int32', buffer, shape, strides, 0, order, { 'mode': 'clamp' } ); // $ExpectType ndarray
	x = new NestedArray( 'int32', buffer, shape, strides, 0, order, { 'mode': 'clamp' } ); // $ExpectType ndarray

	x = NestedArray( 'int32', buffer, shape, strides, 0, order, { 'submode': [ 'throw' ] } ); // $ExpectType ndarray
	x = new NestedArray( 'int32', buffer, shape, strides, 0, order, { 'submode': [ 'throw' ] } ); // $ExpectType ndarray

	if ( x.shape[ 0 ] !== x.shape[ 0 ] ) {
		throw new Error( 'unexpected error' );
	}
}

// The compiler throws an error if the function is provided a first argument which is not a recognized data type...
{
	const buffer = [ 1, 2, 3, 4 ];
	const shape = [ 2, 2 ];
	const strides = [ 2, 1 ];
	const offset = 0;
	const order = 'row-major';

	NestedArray( 'abc', buffer, shape, strides, offset, order ); // $ExpectError
	NestedArray( 123, buffer, shape, strides, offset, order ); // $ExpectError
	NestedArray( true, buffer, shape, strides, offset, order ); // $ExpectError
	NestedArray( false, buffer, shape, strides, offset, order ); // $ExpectError
	NestedArray( null, buffer, shape, strides, offset, order ); // $ExpectError
	NestedArray( undefined, buffer, shape, strides, offset, order ); // $ExpectError
}

// The compiler throws an error if the function is provided a second argument which is not an array-like object or buffer...
{
	const shape = [ 2, 2 ];
	const strides = [ 2, 1 ];
	const offset = 0;
	const order = 'row-major';
	NestedArray( 'float64', 123, shape, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', true, shape, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', false, shape, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', null, shape, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', undefined, shape, strides, offset, order ); // $ExpectError
}

// The compiler throws an error if the function is provided a third argument which is not an array-like object containing numbers...
{
	const buffer = [ 1, 2, 3, 4 ];
	const strides = [ 2, 1 ];
	const offset = 0;
	const order = 'row-major';
	NestedArray( 'float64', buffer, true, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', buffer, false, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', buffer, null, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', buffer, undefined, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', buffer, '5', strides, offset, order ); // $ExpectError
	NestedArray( 'float64', buffer, [ '1', '2' ], strides, offset, order ); // $ExpectError
	NestedArray( 'float64', buffer, {}, strides, offset, order ); // $ExpectError
	NestedArray( 'float64', buffer, ( x: number ): number => x, strides, offset, order ); // $ExpectError
}

