// The compiler throws an error if the function is provided a `mode` option which is not a recognized mode...
{
	const buffer = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
	const shape = [ 2, 2 ];
	const strides = [ 2, 1 ];
	const offset = 0;
	const order = 'row-major';
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': 'abc' } ); // $ExpectError
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': 123 } ); // $ExpectError
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': true } ); // $ExpectError
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': false } ); // $ExpectError
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': null } ); // $ExpectError
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': [] } ); // $ExpectError
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': {} } ); // $ExpectError
	NestedArray( 'float64', buffer, shape, strides, offset, order, { 'mode': ( x: number ): number => x } ); // $ExpectError
}

