// The compiler throws an error if the function is provided and invalid number of arguments...
{
	const buffer = [ 1, 2, 3, 4 ];
	const shape = [ 2, 2 ];
	const strides = [ 2, 1 ];
	const offset = 0;
	NestedArray(); // $ExpectError
	NestedArray( 'uint32' ); // $ExpectError
	NestedArray( 'int8', buffer ); // $ExpectError
	NestedArray( 'uint8c', buffer, shape ); // $ExpectError
	NestedArray( 'uint8', buffer, shape, strides ); // $ExpectError
	NestedArray( 'uint16', buffer, shape, strides, offset ); // $ExpectError
	NestedArray( 'uint16', buffer, shape, strides, offset, 'row-major', {}, {} ); // $ExpectError
}
