'use strict';

// MODULES //

var Uint32Array = require( '@stdlib/array/uint32' );
var Float32Array = require( '@stdlib/array/float32' );
var float64ToFloat32 = require( '@stdlib/number/float64/base/to-float32' );
var PINF = require( '@stdlib/constants/float32/pinf' );
var NINF = require( '@stdlib/constants/float32/ninf' );
var LOW = require( './low.js' );


// VARIABLES //

var FLOAT32_VIEW = new Float32Array( 1 );
var UINT32_VIEW = new Uint32Array( FLOAT32_VIEW.buffer );


// MAIN //

/**
* Sets the less significant 32 bits of a single-precision floating-point number.
*
* ## Notes
*
* ```text
* float32 (32 bits)
* f := fraction (significand/mantissa) (23 bits)
* e := exponent (8 bits)
* s := sign bit (1 bit)
*
* |-------- -------- -------- --------|
* |              Float32              |
* |-------- -------- -------- --------|
* |              Uint32               |
* |-------- -------- -------- --------|
* ```
*
* If little endian (more significant bits last):
*
* ```text
*                         <-- lower      higher -->
* |   f7       f6       f5       f4       f3       f2    e2 | f1 |s|  e1  |
* ```
*
* If big endian (more significant bits first):
*
* ```text
*                         <-- higher      lower -->
* |s| e1    e2 | f1     f2       f3       f4       f5        f6      f7   |
* ```
*
* In which Uint32 can we find the lower order bits? If little endian, the first; if big endian, the second.
*
* ## References
*
* -   [Open Group][1]
*
* [1]: http://pubs.opengroup.org/onlinepubs/9629399/chap14.htm
*
* @param {number} x - single
* @param {uinteger32} low - unsigned 32-bit integer to replace the lower order word of `x`
* @returns {number} single having the same higher order word as `x`
*
* @example
* var low = 5 >>> 0; // => 00000000000000000000000000000101
*
* var x = 3.14e20; // => 0 10010110011 00100000000000000000
*
* var y = setLowWord( x, low ); // => 0 10010110011 00000000000000000101
* // returns 3.139998651394392e+20
*
* @example
* var PINF = require( '@stdlib/constants/float32/pinf' );
* var NINF = require( '@stdlib/constants/float32/ninf' );
*
* var low = 12345678;
*
* var y = setLowWord( PINF, low );
* // returns NaN
*
* y = setLowWord( NINF, low );
* // returns NaN
*
* y = setLowWord( NaN, low );
* // returns NaN
*/
function setLowWord( x, low ) {
	x = float64ToFloat32( x ); // explicitly convert to a single-precision floating-point number
	if ( x === PINF || x === NINF || isNaN(x) ) {
		return NaN;
	}
	FLOAT32_VIEW[ 0 ] = x;
	UINT32_VIEW[ LOW ] = ( low >>> 0 ); // identity bit shift to ensure integer
	return FLOAT32_VIEW[ 0 ];
}


// EXPORTS //

module.exports = setLowWord;
