'use strict';

// MODULES //

var addon = require( './../src/addon.node' );


// MAIN //

/**
* Evaluates the cumulative distribution function (CDF) for a binomial distribution with number of trials `n` and success probability `p` at a value `k`.
*
* @private
* @param {number} k - input value
* @param {number} n - number of trials
* @param {number} p - success probability
* @returns {Probability} evaluated CDF
*
* @example
* var y = cdf( 5, 10, 0.5 );
* // returns ~0.623
*
* @example
* var y = cdf( 3, 10, 0.1 );
* // returns ~0.987
*
* @example
* var y = cdf( 0, 10, 0.5 );
* // returns ~0.00098
*
* @example
* var y = cdf( 5, 10, NaN );
* // returns NaN
*/
function cdf( k, n, p ) {
	return addon( k, n, p );
}


// EXPORTS //

module.exports = cdf;
