#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/factorial.h"
#include "stdlib/math/base/special/pow.h"
#include "stdlib/stats/base/dists/binomial/cdf.h"
#include <stdint.h>
#include <math.h>

/**
* Evaluates the cumulative distribution function (CDF) for a binomial distribution with number of trials `n` and success probability `p` at a value `k`.
*
* @param k    input value
* @param n    number of trials
* @param p    success probability
* @return     evaluated CDF
*
* @example
* double y = stdlib_base_dists_binomial_cdf( 5, 10, 0.5 );
* // returns ~0.623
*/
double stdlib_base_dists_binomial_cdf( const int64_t k, const int64_t n, const double p ) {

	if ( stdlib_base_is_nan( k ) || stdlib_base_is_nan( n ) || stdlib_base_is_nan( p ) || n < 0 || p < 0.0 || p > 1.0 ) {
		return 0.0 / 0.0; // NaN
	}
	if ( k < 0 ) {
		return 0.0;
	}
	if ( k >= n ) {
		return 1.0;
	}

	double cdf = 0.0;
	for ( int i = 0; i <= k; i++ ) {
		double binom_coeff = stdlib_base_factorial( n ) / ( stdlib_base_factorial( i ) * stdlib_base_factorial( n - i ) );
		cdf += binom_coeff * stdlib_base_pow( p, i ) * stdlib_base_pow( 1.0 - p, n - i );
	}
	return cdf;
}
