// TypeScript Version: 2.0

/**
* Invokes a function after a specified duration.
*
* @param fcn - function to invoke
* @param duration - duration
* @param args - arguments to invoke function with
* @returns timeout object
*
* @example
* function beep() {
*     console.log( 'beep' );
* }
*
* var t = delay( beep, 3000 );
* // returns <Object>
*
* @example
* function beep() {
*     console.log( 'beep' );
* }
*
* var t = delay( beep, '3s' );
* // returns <Object>
*
* @example
* function add( x, y ) {
*     var sum = x + y;
*     console.log( 'sum: %d', sum );
* }
*
* var t = delay( add, 3000, 2, 3 );
* // returns <Object>
*/
declare function delay<T extends Array<any>, U>( fcn: ( ...args: T ) => U, duration: NonNegativeInteger | string, ...args: T ): NodeJS.Timeout; // tslint-disable-line max-line-length

